import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import colorSharp from "../assets/img/color-sharp.png";
import { FaHtml5, FaCss3Alt, FaJs, FaReact, FaNodeJs, FaDatabase, FaCsharp } from "react-icons/fa";

export const Skills = () => {
  const responsive = {
    superLargeDesktop: { breakpoint: { max: 4000, min: 3000 }, items: 5 },
    desktop: { breakpoint: { max: 3000, min: 1024 }, items: 3 },
    tablet: { breakpoint: { max: 1024, min: 464 }, items: 2 },
    mobile: { breakpoint: { max: 464, min: 0 }, items: 1 }
  };

  const skills = [
    { name: "HTML", icon: <FaHtml5 size={60} color="#E34F26" /> },
    { name: "CSS", icon: <FaCss3Alt size={60} color="#1572B6" /> },
    { name: "JavaScript", icon: <FaJs size={60} color="#F7DF1E" /> },
    { name: "React", icon: <FaReact size={60} color="#61DAFB" /> },
    { name: "Node.js", icon: <FaNodeJs size={60} color="#83CD29" /> },
    { name: "SQL", icon: <FaDatabase size={60} color="#4479A1" /> },
    { name: "C#", icon: <FaCsharp size={60} color="#239120" /> },
  ];

  return (
    <section className="skill" id="skills">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="skill-bx wow zoomIn">
              <h2>Skills</h2>
              <p>Estas son algunas de las tecnolog√≠as en las que tengo experiencia.<br></br> Desarrollo tanto en frontend como en backend.</p>
              <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme skill-slider">
                {skills.map((skill, index) => (
                  <div className="item" key={index}>
                    {skill.icon}
                    <h5>{skill.name}</h5>
                  </div>
                ))}
              </Carousel>
            </div>
          </div>
        </div>
      </div>
      <img className="background-image-left" src={colorSharp} alt="Background" />
    </section>
  );
};
